using System;
using System.CodeDom;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Interview.LeetCode
{
    class Question1100
    {
        public static void EntryPoint()
        {
            (new Question1100()).NumKLenSubstrNoRepeats("", 1);
        }

        public int NumKLenSubstrNoRepeats(string S, int K)
        {
            if (K == 1)
                return S.Length;

            int result = 0,
                start = 0,
                end = 0;
            int[] index = new int[26];

            for (int i = 0; i < 26; i++)
                index[i] = Int32.MaxValue;

            if (S.Length >= K)
                while (end < S.Length)
                {
                    if (end - start + 1 == K && index[S[end] - 'a'] == Int32.MaxValue)
                    {
                        result++;
                        index[S[start++] - 'a'] = Int32.MaxValue;
                    }
                    else if (index[S[end] - 'a'] != Int32.MaxValue)
                    {
                        int i = start;
                        start = index[S[end] - 'a'] + 1;

                        while (i < start)
                            index[S[i++] - 'a'] = Int32.MaxValue;

                        index[S[start] - 'a'] = start;
                    }

                    index[S[end] - 'a'] = end++;
                }

            return result;
        }
    }
}
