        public IList<IList<int>> ThreeSum(int[] nums)
        {
            List<IList<int>> result = new List<IList<int>>();
            List<int> tempList = null;
            int num1Index = 0,
                num2Index = 0,
                num3Index = 0,
                sum = 0;

            if (nums != null && nums.Length != 0)
            {
                Array.Sort(nums);

                for (num1Index = 0; num1Index < nums.Length - 1; num1Index++)
                {
                    if (num1Index != 0 && nums[num1Index - 1] == nums[num1Index])
                        continue;

                    num2Index = num1Index + 1;
                    num3Index = nums.Length - 1;

                    while (num2Index < num3Index)
                    {
                        sum = nums[num1Index] + nums[num2Index] + nums[num3Index];

                        if ((num2Index - 1 != num1Index && nums[num2Index - 1] == nums[num2Index]))
                        {
                            num2Index++;
                            continue;
                        }
                        else if (num3Index + 1 != nums.Length && nums[num3Index + 1] == nums[num3Index])
                        {
                            num3Index--;
                            continue;
                        }
                        else if (sum == 0)
                        {
                            tempList = new List<int>();
                            tempList.Add(nums[num1Index]);
                            tempList.Add(nums[num2Index]);
                            tempList.Add(nums[num3Index]);

                            result.Add(tempList);

                            num2Index++;
                            num3Index--;
                        }
                        else if (sum < 0)
                            num2Index++;
                        else if (sum > 0)
                            num3Index--;
                    }
                }
            }

            return result;
        }
    }
